var modalForm=document.querySelector(".modal-basket"),modalOverlay=document.querySelector(".modal-overlay"),modalOpen=document.querySelector(".modal-open"),modalClose=document.querySelector(".modal-close");modalOpen.addEventListener("click",function(e){e.preventDefault(),modalForm.classList.add("modal-basket--open"),modalOverlay.classList.add("modal-overlay--open")}),modalClose.addEventListener("click",function(e){e.preventDefault(),modalForm.classList.remove("modal-basket--open"),modalOverlay.classList.remove("modal-overlay--open")}),window.addEventListener("keydown",function(e){27===e.keyCode&&(e.preventDefault(),modalForm.classList.contains("modal-basket--open")&&(modalForm.classList.remove("modal-basket--open"),modalOverlay.classList.remove("modal-overlay--open")))});
